import { Component } from '@angular/core';
import {Book} from "./dto/book";
import {HttpClient} from "@angular/common/http";
import {timeout} from "rxjs";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'front-end';
  bookList: Array<Book> = [];
  isDone: boolean = false;
  status: string = '';
  description: string= '';
  isIsbnToEdit: boolean= false;

  constructor(private httpClient: HttpClient) {
    httpClient.get<Array<Book>>('http://localhost:8080/api/v1/books').subscribe(bookList => {
      this.bookList = bookList;
    });
  }

  deleteBook(book: Book) {
    this.httpClient.delete(`http://localhost:8080/api/v1/books/${book.isbn}`).subscribe(value => {
      const index = this.bookList.indexOf(book);
      this.bookList.splice(index, 1);
      this.showToast(true,"Deleting Done",`Deleting of the book ${book.isbn} is done!`);
    });
  }

  editBook(book: Book, isbn: HTMLInputElement, title: HTMLInputElement) {
    if(!title.value.trim()){
      title.select();
      return
    }
    if(!isbn.value.trim()){
      title.select();
      this.isIsbnToEdit = true;
      return
    }
    isbn.value = book.isbn;
    let book1:Book = new Book(book.isbn,title.value.trim());
    this.httpClient.put(`http://localhost:8080/api/v1/books`,book1).subscribe(value => {
        const index = this.bookList.indexOf(book);
        this.bookList[index] = book1;
      this.showToast(true,"Editing Done",`Editing of the book ${book.isbn} is done!`);
    })
    isbn.value ='';
    title.value='';
  }

  saveBook(isbn: HTMLInputElement, title: HTMLInputElement) {
    if(!title.value.trim()){
      title.select();
      return
    }
    if(!title.value.trim()){
      title.select();
      return
    }
    let book:Book = new Book(isbn.value.trim(),title.value.trim());
    this.httpClient.post<Book>('http://localhost:8080/api/v1/books',book).subscribe(value => {
      this.bookList.push(book);
      this.showToast(true,"Saving Done",`Saving of the book ${book.isbn} is done!`);
      isbn.value = '';
      title.value = '';
    })
  }

  toastClose() {
    this.isDone =false;
  }
  showToast(isDone:boolean,status:string,description:string){
    this.isDone =isDone;
    this.status = status;
    this.description = description;
    setTimeout(()=>{
      this.isDone = false;
      this.status = '';
      this.description = '';
    },3000);
  }
}
<div  class="container">
  <div id="app-title" class="d-flex justify-content-center gap-3 p-3 align-content-center">
    <h1>Lib. User</h1>
    <svg xmlns="http://www.w3.org/2000/svg" width="36" height="36" fill="currentColor" class="bi bi-collection mt-2" viewBox="0 0 16 16">
      <path d="M2.5 3.5a.5.5 0 0 1 0-1h11a.5.5 0 0 1 0 1h-11zm2-2a.5.5 0 0 1 0-1h7a.5.5 0 0 1 0 1h-7zM0 13a1.5 1.5 0 0 0 1.5 1.5h13A1.5 1.5 0 0 0 16 13V6a1.5 1.5 0 0 0-1.5-1.5h-13A1.5 1.5 0 0 0 0 6v7zm1.5.5A.5.5 0 0 1 1 13V6a.5.5 0 0 1 .5-.5h13a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-.5.5h-13z"/>
    </svg>
  </div>
  <form>
    <div class="mb-3">
      <label for="isbn" class="form-label">ISBN</label>
      <input #isbn type="text" class="form-control" id="isbn" aria-describedby="isbnHelp">
          <div *ngIf="isIsbnToEdit" id="isbnHelp" class="form-text">We'll get the selected book's isbn for editing the title. So don't add any isbn for editing.</div>
    </div>
    <div class="mb-3">
      <label for="title" class="form-label">Title</label>
      <input #title type="password" class="form-control" id="title">
    </div>
    <button (click)="saveBook(isbn,title)" class="btn btn-primary">ADD</button>
  </form>
  <div id="table-container">
    <table class="table table-hover">
      <thead>
      <tr>
        <th scope="col">#</th>
        <th scope="col">Name</th>
        <th scope="col">ISBN</th>
        <th scope="col"></th>
      </tr>
      </thead>
      <tbody>
      <ng-container *ngFor="let book of bookList">
        <tr>
          <th scope="row">1</th>
          <td>{{book.name}}</td>
          <td>{{book.isbn}}</td>
          <td>
            <div class="d-flex justify-content-evenly">
              <div id="delete" (click)="deleteBook(book)">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-trash3-fill" viewBox="0 0 16 16">
                  <path d="M11 1.5v1h3.5a.5.5 0 0 1 0 1h-.538l-.853 10.66A2 2 0 0 1 11.115 16h-6.23a2 2 0 0 1-1.994-1.84L2.038 3.5H1.5a.5.5 0 0 1 0-1H5v-1A1.5 1.5 0 0 1 6.5 0h3A1.5 1.5 0 0 1 11 1.5Zm-5 0v1h4v-1a.5.5 0 0 0-.5-.5h-3a.5.5 0 0 0-.5.5ZM4.5 5.029l.5 8.5a.5.5 0 1 0 .998-.06l-.5-8.5a.5.5 0 1 0-.998.06Zm6.53-.528a.5.5 0 0 0-.528.47l-.5 8.5a.5.5 0 0 0 .998.058l.5-8.5a.5.5 0 0 0-.47-.528ZM8 4.5a.5.5 0 0 0-.5.5v8.5a.5.5 0 0 0 1 0V5a.5.5 0 0 0-.5-.5Z"/>
                </svg>
              </div>
              <div id="edit" (click)="editBook(book,isbn,title)">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-pencil-square" viewBox="0 0 16 16">
                  <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z"/>
                  <path fill-rule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z"/>
                </svg>
              </div>
            </div>
          </td>
        </tr>
      </ng-container>
      </tbody>
    </table>
  </div>
  <ng-container *ngIf="isDone">
    <div id="toast" class="toast-container position-fixed bottom-0 start-50 translate-middle-x p-3">
      <div id="liveToast" class="toast show" data-bs-autohide="true" data-bs-delay="1000" role="alert" aria-live="assertive" aria-atomic="true">
        <div class="toast-header">
          <strong class="me-auto">{{status}}</strong>
          <button (click)="toastClose()" type="button" class="btn-close" data-bs-dismiss="toast" aria-label="Close">{{description}}</button>
        </div>
        <div class="toast-body">
        </div>
      </div>
    </div>
  </ng-container>
</div>
